Bash(php artisan tinker --execute="
use App\\Models\\Vote;
use App\\Models\\User;
use App\\Models\\Candidate;
use Illuminate\\Support\\Facades\\DB;

DB::transaction\(function \(\) {
    $votesDeleted = Vote::count\(\);
    Vote::truncate\(\);

    Candidate::query\(\)->update\([''votes_count'' => 0]\);

    $usersDeleted = User::count\(\);
    User::truncate\(\);

    echo ""Eliminados: {$usersDeleted} votantes, {$votesDeleted} votos. Contadores de candidatos reseteados a 0.\\n"";
}\);
")
